import React, { useState } from "react";
import Header from "./MyComponents/Header";
import Todo_list from "./MyComponents/Todo_list";
import Footer from "./MyComponents/Footer";
import { Button, Modal, Form } from "react-bootstrap";


const App = () => {

  const Delete = (todo) =>{
    setTodos(
      todos.filter((_todo)=>{
        return _todo!==todo;
      })
    );
  }

  const [show, setShow] = useState(false); // State to control modal visibility
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    message: "",
  });


  const [todos , setTodos] = useState([
    {
      sno: 1,
      title: "Go to the market",
      desc: "You need to go to the market to get this job done"
    },
    {
      sno: 2,
      title: "Go the College",
      desc: "Attend Lectures"
    },
    {
      sno: 3,
      title: "Complete the website",
      desc: "Learn and Code"
    },
    {
      sno: 4,
      title: "Go to the market",
      desc: "You need to go to the market to get this job done"
    },
    {
      sno: 5,
      title: "Go the College",
      desc: "Attend Lectures"
    },
    {
      sno: 6,
      title: "Complete the website",
      desc: "Learn and Code"
    },
    {
      sno: 7,
      title: "Go to the market",
      desc: "You need to go to the market to get this job done"
    },
    {
      sno: 8,
      title: "Go the College",
      desc: "Attend Lectures"
    },
    {
      sno: 9,
      title: "Complete the website",
      desc: "Learn and Code"
    }
  ]);

  return (
    <>
    <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh', paddingBottom : 20}}>
      <Header title = "My Todo's"  searchBar = {true}/>
      <Todo_list Todo = {todos} OnDelete = {Delete}/> 
    </div> 
      <Footer/>
    </>
  );
};

export default App;
