import React, { useState  , useEffect} from "react";
import Header from "./MyComponents/Header";
import Todo_list from "./MyComponents/Todo_list";
import Footer from "./MyComponents/Footer";
import Form from "./MyComponents/form";
import "./App.css";

const App = () => {
    const Delete = async (id) => {
        try {
            const response = await fetch(`http://localhost:2000/data/${id}`, {
                method: 'DELETE',
            });

            const data = await response.json();

            if (response.ok) {
                console.log('Item marked as deleted:', data);
                // Update the state to reflect the deleted status
                setTodos((prevTodos) =>
                    prevTodos.map((todo) =>
                        todo._id === id ? { ...todo, isDeleted: true } : todo
                    )
                );
            } else {
                console.error('Error updating item:', data.message);
            }
        } catch (err) {
            console.error('Error updating item:', err);
        }
    };

     


  const [count , incrementCount] = useState(1);

  const increment = ()=>{
     incrementCount(
        count + 1
     )
  };

  const addTodo = (todo) =>{
    setTodos([...todos , todo]);
  };

  const [todos , setTodos] = useState([

  ]);

    useEffect(() => {
        fetch('http://localhost:2000/')
            .then(response => response.json())  
            .then(async data => {
                setTodos(data);  
            })
            .catch(err => console.log(err));
    }, []);

  const [isOpen, setIsOpen] = useState(false);

  return (
    <>

 <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh', paddingBottom : 20}}>
       <Header setIsOpen={setIsOpen}  title = "My Todo's"  searchBar = {true}/>
       <Form isOpen={isOpen} setIsOpen= {setIsOpen} count = {count} Increment = {increment}  _todos = {todos}  _setTodos = {addTodo}/>
       <Todo_list Todo = {todos} OnDelete = {Delete}/>   
     </div> 
     <Footer />
    </>
  );
};

 


export default App;
