import React, { useState } from 'react';

const FormPopup = (props) => {
  const [isOpen, setIsOpen] = useState(false);
  const [submissions, setSubmissions] = useState([]);
  const [formData, setFormData] = useState({
    Title: '',
    desc: '',   
  });

  const handleSubmit = (e) => {
    e.preventDefault();
    setSubmissions([...submissions, { ...formData, id: props.count }]);
    setFormData({ Title: '', desc: '' });
    setIsOpen(false);
  };

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData(prev => ({
      ...prev,
      [name]: value
    }));
  };

   

  return (
    <div>
      <button 
        onClick={() => setIsOpen(true)}
        style={modalStyles.button}
      >
        Open Form
      </button>

      <div style={modalStyles.overlay}>
        <div style={modalStyles.modal}>
          <h2 style={{ marginBottom: '20px' }}>Add-Todo</h2>
          <form onSubmit={handleSubmit}>
            <div>
              <label htmlFor="Title">Title</label>
              <input
                id="Title"
                name="Title"
                value={formData.Title}
                onChange={handleChange}
                required
                style={modalStyles.input}
              />
            </div>
            <div>
              <label htmlFor="desc">Description</label>
              <input
                id="desc"
                name="desc"
                type="desc"
                value={formData.desc}
                onChange={handleChange}
                required
                style={modalStyles.input}
              />
            </div>
            <div style={{ display: 'flex', gap: '10px', justifyContent: 'flex-end' }}>
              <button 
                type="button" 
                onClick={() => setIsOpen(false)}
                style={{ ...modalStyles.button, backgroundColor: '#6c757d' }}
              >
                Cancel
              </button>
              <button 
                type="submit"
                style={modalStyles.button}
              >
                Submit
              </button>
            </div>
          </form>
        </div>
      </div>

      {/* Display submitted information */}
      <div style={{ marginTop: '32px' }}>
        <h2 style={{ marginBottom: '16px' }}>Add-Todo</h2>
        {submissions.map(submission => (
          <div key={submission.id} style={modalStyles.card}>
            <p><strong>Name:</strong> {submission.Title}</p>
            <p><strong>Description:</strong> {submission.desc}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default FormPopup;
